// src/nodes/TextNode.js
import React, { useEffect, useMemo, useRef, useState } from "react";
import { Handle, Position, useReactFlow } from "reactflow";

const VAR_RE = /\{\{\s*([A-Za-z_$][A-Za-z0-9_$]*)\s*\}\}/g;

export default function TextNode({ id, data }) {
  const [value, setValue] = useState(data?.value || "");
  const [vars, setVars] = useState([]);
  const taRef = useRef(null);
  const [size, setSize] = useState({ w: 260, h: 140 });
  const { setNodes } = useReactFlow();

  // detect variables {{var}}
  useEffect(() => {
    const found = new Set();
    let m;
    while ((m = VAR_RE.exec(value)) !== null) found.add(m[1]);
    setVars([...found]);
  }, [value]);

  // autosize textarea & node
  useEffect(() => {
    if (!taRef.current) return;
    const TA = taRef.current;
    TA.style.height = "0px";
    TA.style.height = TA.scrollHeight + "px";
    const lines = TA.value.split("\n");
    const longest = lines.reduce((a, b) => (b.length > a ? b.length : a), 0);
    const w = Math.min(520, Math.max(260, longest * 7 + 40));
    const h = Math.min(420, Math.max(140, TA.scrollHeight + 80));
    setSize({ w, h });
  }, [value]);

  // push updates back into node data
  useEffect(() => {
    setNodes((nds) =>
      nds.map((n) => (n.id === id ? { ...n, data: { ...n.data, value } } : n))
    );
  }, [value, id, setNodes]);

  const inputs = useMemo(
    () => vars.map((name) => ({ id: `var:${name}`, label: name })),
    [vars]
  );

  return (
    <div className="node-card" style={{ width: size.w, minHeight: size.h }}>
      <div className="node-header">
        <div className="node-title">Text</div>
        <div className="badge">
          {vars.length} var{vars.length === 1 ? "" : "s"}
        </div>
      </div>

      <div className="node-body">
        <textarea
          ref={taRef}
          className="node-textarea"
          placeholder="Type here... use {{ variableName }} to create inputs"
          value={value}
          onChange={(e) => setValue(e.target.value)}
        />
        {vars.length > 0 && (
          <div
            style={{ marginTop: 8, display: "flex", gap: 6, flexWrap: "wrap" }}
          >
            {vars.map((v) => (
              <span key={v} className="badge">
                {v}
              </span>
            ))}
          </div>
        )}
      </div>

      {/* Left handles for variables */}
      {inputs.map((h, idx) => (
        <Handle
          key={`in-${h.id}`}
          id={h.id}
          type="target"
          position={Position.Left}
          style={{
            top: 56 + idx * 22,
            background: "#555", // bigger, darker handle
            width: 12,
            height: 12,
            borderRadius: "50%",
          }}
        />
      ))}

      {/* Single output */}
      <Handle
        id={`${id}-out`}
        type="source"
        position={Position.Right}
        style={{
          top: size.h / 2,
          background: "green", // output handle color
          width: 14,
          height: 14,
          borderRadius: "50%",
        }}
      />
    </div>
  );
}
